const {data,fields} = primo
const {fields:headerFields} = fields[0]
const labels = headerFields.map(f => f.label)
const values = data.people.map(person => [ person.name, person.phone, person.email ])

new Grid({ 
  columns: labels,
  data: values
}).render(document.getElementById(`table-${data.id}`));

