styles.raw:
.primo-content {
    @apply text-gray-800 text-xl;
    h1 {
        @apply text-5xl font-bold;
    }
    h2 {
        @apply text-4xl font-bold; 
    }
}

#column-npuvs {
    @apply flex flex-col justify-center;
    @media screen and (max-width: 800px) {
        @apply pt-8;
    }
}

#section-npbdm {
    @apply bg-white py-16;
}

#section-janqe {
    @apply bg-blue-500 py-16;

    .primo-content {
        @apply text-gray-100;   

        div:first-child {
            @apply text-3xl font-bold;
        }
    }
}


/*
#section-zasa5 > div {
    max-width: 900px;
    @apply py-16;

    #column-ow8j8 {
        display: flex;
        align-items: center;
        padding-right: 2rem !important;

        h2 {
            @apply text-2xl;
        }
    }
}
*/

#section-hn4ee > div {
    @apply py-16;
    max-width: 600px;

    h2 {
        @apply text-2xl;
    }
}

