const introducing = select('first')
const theAll = select('second')
const IDE = select('third')
const CMS = select('fourth')
const CompLibrary = select('fifth')
const StaticSite = select('sixth')

let startDelay = 0;

setTimeout(() => {
    fadein('first');
}, startDelay)

setTimeout(() => {
	hide('first')    
    theAll.classList.add('visible')

    setTimeout(() => {
    	theAll.classList.remove('visible')
        IDE.classList.add('visible');
	}, 1500)

    setTimeout(() => {
    	IDE.classList.remove('visible')
        CMS.classList.add('visible');
	}, 2000)

    setTimeout(() => {
    	CMS.classList.remove('visible')
        CompLibrary.classList.add('visible');
	}, 2500)

    setTimeout(() => {
    	CompLibrary.classList.remove('visible')
        show('staticsite')
	}, 3000)

    setTimeout(() => {
    	hide('staticsite')
	}, 3500)

    setTimeout(() => {
    	fadein('whatcanyoudo')
	}, 4500)

    setTimeout(() => {
    	hide('whatcanyoudo')
	}, 7500)

    setTimeout(() => {
    	show('gettothecode')
    	show('get')
        setTimeout(() => { show('to') }, 210)
        setTimeout(() => { show('the') }, 420)
        setTimeout(() => { show('code') }, 640)

	}, 8200)

    setTimeout(() => {
    	hide('gettothecode')
    	hide('get')
    	hide('to')
    	hide('the')
    	hide('code')

        show('editwithease')
    	show('ed')
        setTimeout(() => { show('it') }, 210)
        setTimeout(() => { show('with') }, 420)
        setTimeout(() => { show('ease') }, 640)
        setTimeout(() => { show('finally') }, 1000)
	}, 9500)

    setTimeout(() => {
		hide('editwithease')
    	hide('ed')
    	hide('it')
    	hide('with')
    	hide('ease')
    	hide('finally')

        show('andsomuchmore')
    	show('and')
        setTimeout(() => { show('so') }, 210)
        setTimeout(() => { show('much') }, 420)
        setTimeout(() => { show('more') }, 640)
	}, 12000)

    setTimeout(() => {
		hide('andsomuchmore')
    	hide('and')
    	hide('so')
    	hide('much')
    	hide('more')

        show('forfreechamp')
        show('forfree')
	}, 14000)

   setTimeout(() => {
        show('champ')
	}, 15000)

    setTimeout(() => {
		hide('forfreechamp')
		hide('forfree')
		hide('champ')

        show('love')
	}, 17000)

    setTimeout(() => {
		hide('love')
        show('khru')
	}, 20000)


}, startDelay + 1500)


function select(id) {
	return document.querySelector(`#slide #${id}`)
}

function fadein(id) {
	select(id).classList.add('fadein')
}

function show(id) {
	select(id).classList.add('visible')
}

function hide(id) {
	select(id).classList.remove('visible')
	select(id).classList.remove('fadein')
}

