styles.raw:
/* Default content styles */
.primo-content {
  @apply text-lg;
  h1 {
    @apply text-3xl font-medium;
  }
  h2 {
    @apply text-2xl font-medium;
  }
  ul {
    @apply list-disc list-inside;
    p {
        @apply inline;
    }
  } 
  ol {
    @apply list-decimal list-inside;
  } 
  blockquote {
      @apply shadow-md p-6;
  }
  mark {
    @apply text-gray-900 bg-yellow-200;
  }

  @screen lg {
    h1 {
      @apply text-5xl;
    }
    h2 {
      @apply text-4xl;
    }
  }
}

raw.js:
var firebaseConfig = {
    apiKey: "AIzaSyCgmv4xD8A1jg2KVc_L6qQUagURzI33vdA",
    authDomain: "showmethumbs.firebaseapp.com",
    databaseURL: "https://showmethumbs.firebaseio.com",
    projectId: "showmethumbs",
    storageBucket: "showmethumbs.appspot.com",
    messagingSenderId: "299019888957",
    appId: "1:299019888957:web:674941c3637c2724043d54"
};

// Initialize Firebase
window.firebase.initializeApp(firebaseConfig);

const db = window.firebase.firestore()

const docRef = db.collection("classrooms").doc('mkyo')

function sendThumb(thumb) {
    docRef.update({
        thumbs: window.firebase.firestore.FieldValue.arrayUnion({
            student: 'Patricia',
            thumb
        })
    });
}

window.sendThumb = sendThumb

