    	@apply bg-green-500 text-white py-4 px-8 rounded-full no-underline uppercase text-sm tracking-wide;
    }
}

/* Drinks */
#section-apau2 {
	@apply py-16 my-16 bg-cover bg-center relative;
    &:before {
        content: '';
        position: absolute;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        z-index: 0;
        background: rgba(0,0,0,0.5);
    }
    &:after {
    	@apply bg-cover bg-center;
    	content: '';
        position: absolute;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        z-index: -1;
		background-image: url('https://images.unsplash.com/photo-1514361726087-38371321b5cd?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80');
    }
    h1 {
    	margin-bottom: 0;
    }
    h1, h2, h3, h4, h5, h6, h6 {
    	@apply font-bold;
        font-family: Lato, serif !important;
    }
    .primo-content {
    	@apply text-gray-100;
    }
}

styles.final:
/* Default content styles */

.primo-content {
  font-size: 1.125rem;
  --text-opacity: 1;
  color: #1a202c;
  color: rgba(26, 32, 44, var(--text-opacity));
}

.primo-content h1 {
    font-size: 1.875rem;
    font-weight: 700;
    line-height: 1;
    margin-bottom: 1rem;
  }

.primo-content h2 {
    font-size: 1.5rem;
    font-weight: 500;
  }

.primo-content ul {
    list-style-type: disc;
    list-style-position: inside;
  }

.primo-content ul p {
      display: inline;
    }

.primo-content ol {
    list-style-type: decimal;
    list-style-position: inside;
  }

.primo-content a {
    --text-opacity: 1;
    color: #3182ce;
    color: rgba(49, 130, 206, var(--text-opacity));
    text-decoration: underline;
  }

.primo-content blockquote {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    padding: 1.5rem;
  }

.primo-content mark {
    --text-opacity: 1;
    color: #1a202c;
    color: rgba(26, 32, 44, var(--text-opacity));
    --bg-opacity: 1;
    background-color: #fefcbf;
    background-color: rgba(254, 252, 191, var(--bg-opacity));
  }

@media (min-width: 1024px) {
    .primo-content h1 {
